import React from 'react';
import { useEffect, useState } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import { Routes, Route, useNavigate} from "react-router-dom";
import {api} from '../utils/api.js';
import * as authApi from '../utils/authApi'
import ProtectedRoute from './ProtectedRoute';
import InfoToolTip from './InfoToolTip';
import NotFound from './NotFound';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import ImagePopup from './ImagePopup.js';
import EditProfilePopup from './EditProfilePopup.js';
import EditAvatarPopup from './EditAvatarPopup.js';
import AddPlacePopup from './AddPlacePopup.js';
import ConfirmPopup from './ConfirmPopup';
import Register from './Register';
import Login from './Login';

// меню мобайл
import MenuMobile from './MenuMobile';

function App() {

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false)
  const [isConfirmPopupOpen, setIsConfirmPopupOpen] = useState(false);
  const [isImagePopupOpen, setIsImagePopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);
  const [cards, setCards] = useState([]);
  const [currentUser, setCurrentUser] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [deletedCard, setDeletedCard] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [email, setEmail] = useState('');
  const [isRegisterSuccessful, setRegisterSuccessful] = useState(false);
  const [isInfoToolTipOpen, setIsInfoToolTipOpen] = useState(false);
  const navigate = useNavigate();


  // меню мобайл
  const [isShowMenu, setIsShowMenu] = React.useState('menu-mobile_type_close');
  const [classHeaderMenu, setClassHeaderMenu] = React.useState('header__menu_type_closed');

  function tokenCheck() {
    const jwt = localStorage.getItem('jwt');
    if (!jwt) {
      return
    }
    authApi.verifyUser(jwt)
      .then((data) => {
        setEmail(data.data.email);
        setIsLoggedIn(true)
      })
      .catch((err) => {
        console.log(err);
      })
  }

  useEffect(() => {
    tokenCheck();
  }, []);

  useEffect(() => {
    if (isLoggedIn) {
      Promise.all([api.getUserInfo(), api.getInitialCards()])
        .then(([user, cards]) => {
          setCurrentUser(user);
          setCards(cards);
        })
        .catch((err) => {
          console.log(err);
        });
    }
  }, [isLoggedIn])

  useEffect(() => {
    isLoggedIn ? navigate('/react-mesto-auth') : navigate('/sign-in');
  }, [isLoggedIn])


  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function closeAllPopups() {
    setIsAddPlacePopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsConfirmPopupOpen(false);
    setIsInfoToolTipOpen(false);
    setIsImagePopupOpen(false);
    setSelectedCard(null);
    setDeletedCard(null);
  }

  function handleCardClick(card) {
    setIsImagePopupOpen(true);
    setSelectedCard(card);
  }

  function handleUpdateUser(name, description) {
    setIsLoading(true);
    api.editProfile({name, description})
      .then(res => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch(err => {
        console.log(err)
      })
      .finally(() => {
        setIsLoading(false);
      })
  }

  function handleUpdateAvatar(avatar) {
    setIsLoading(true);
    api.updateUserAvatar(avatar)
      .then(res => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch(err => {
        console.log(err);
      })
      .finally(() => {
        setIsLoading(false);
      })
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    if (!isLiked) {
      api.addLike(card._id)
        .then((newCard) => {
          setCards((cards) => cards.map((c) => c._id === card._id ? newCard : c));
        })
        .catch(err => {
          console.log(err)
        });
    } else {
      api.removeLike(card._id)
        .then((newCard) => {
          setCards((cards) => cards.map((c) => c._id === card._id ? newCard : c));
        })
        .catch(err => {
          console.log(err);
        })
    }
  }

  function handleCardDeleteSubmit(card) {
    setIsLoading(true);
    api.deleteCard(card._id)
      .then(() => {
        setCards((cards) => {
          return cards.filter(c => {
            return c._id !== card._id
          });
        });
        closeAllPopups();
      })
      .catch(err => {
        console.log(err);
      })
      .finally(() => {
        setIsLoading(false);
      })
  }

  function handleAddPlaceSubmit(name, link) {
    setIsLoading(true);
    api.addCard({ name, link })
      .then(newCard => {
        setCards([newCard, ...cards]);
        closeAllPopups();
      })
      .catch(err => {
        console.log(err);
      })
      .finally(() => {
        setIsLoading(false);
      })
  }

  function handleConfirmCardDelete(card) {
    setIsConfirmPopupOpen(true);
    setDeletedCard(card);
  }

  function onLogin(password, email) {
    setIsLoading(true);
    authApi.signIn(password, email)
      .then((data) => {
        const jwt = data.token;
        localStorage.setItem('jwt', jwt);
        setIsLoggedIn(true);
        setEmail(email);
      })
      .catch(err => {
        console.log(err);
      })
      .finally(() => {
        setIsLoading(false);
      })
  }

  function onRegister(password, email) {
    setIsLoading(true);
    authApi.signUp(password, email)
      .then((data) => {
        setEmail(data.data.email);
        setIsInfoToolTipOpen(true);
        setRegisterSuccessful(true);
        setIsLoggedIn(true);
      })
      .catch((err) => {
        console.log(err);
        setIsInfoToolTipOpen(true);
        setRegisterSuccessful(false);
      })
      .finally(() => {
        setIsLoading(false);
      })
  }

  function onLogOut() {
    localStorage.removeItem('jwt');
    setIsLoggedIn(false);
  }

  function closeInfoToolTip() {
    closeAllPopups();
    isRegisterSuccessful ? navigate('/react-mesto-auth') : navigate('/sign-up');
  }

  function showMenu() {
    if (isShowMenu === 'menu-mobile_type_close') {
      setIsShowMenu('menu-mobile_type_open');
    } else {
      setIsShowMenu('menu-mobile_type_close');
    }

    if (classHeaderMenu === 'header__menu_type_opened') {
      setClassHeaderMenu('header__menu_type_closed');
    } else {
      setClassHeaderMenu('header__menu_type_opened');
    }
  }


  return (<CurrentUserContext.Provider value={currentUser}>
    {isLoggedIn
      && < MenuMobile
        email={email}
        onLogOut={onLogOut}
        isShowMenu={isShowMenu}
      />
      }


    <Header isLoggedIn={isLoggedIn}
            email={email || ''}
            onLogOut={onLogOut}
            showMenu={showMenu}
        classHeaderMenu={classHeaderMenu}/>
    <Routes>
      <Route path={'/react-mesto-auth'}
             element={<ProtectedRoute isLoggedIn={isLoggedIn}>
               <Main onEditProfile={handleEditProfileClick}
                     onAddPlace={handleAddPlaceClick}
                     onEditAvatar={handleEditAvatarClick}
                     onCardClick={handleCardClick}
                     cards={cards}
                     onCardLike={handleCardLike}
                     onCardDelete={handleConfirmCardDelete}/>
             </ProtectedRoute>}/>
      <Route path={'/sign-up'}
             element={<Register isLoading={isLoading}
                                onRegister={onRegister}/>}
             isLoggedIn={isLoggedIn}/>
      <Route path={'/sign-in'}
             element={<Login onLogin={onLogin}
                             isLoggedIn={isLoggedIn}
                             isLoading={isLoading}/>}/>
      <Route path={'*'}
             element={<NotFound isLoggedIn={isLoggedIn}/>}/>
    </Routes>
    <Footer/>
    <EditProfilePopup isOpen={isEditProfilePopupOpen}
                      onClose={closeAllPopups}
                      onUpdateUser={handleUpdateUser}
                      onLoading={isLoading}/>
    <EditAvatarPopup isOpen={isEditAvatarPopupOpen}
                     onClose={closeAllPopups}
                     onUpdateAvatar={handleUpdateAvatar}
                     onLoading={isLoading}/>
    <AddPlacePopup isOpen={isAddPlacePopupOpen}
                   onClose={closeAllPopups}
                   onAddPlace={handleAddPlaceSubmit}
                   onLoading={isLoading}/>
    <ConfirmPopup isOpen={isConfirmPopupOpen}
                        onClose={closeAllPopups}
                        onLoading={isLoading}
                        card={deletedCard}
                        onSubmit={handleCardDeleteSubmit}/>
    <ImagePopup card={selectedCard}
                isOpen={isImagePopupOpen}
                onClose={closeAllPopups}/>
    <InfoToolTip isSuccess={isRegisterSuccessful}
                 isOpen={isInfoToolTipOpen}
                 onClose={closeInfoToolTip}/>
  </CurrentUserContext.Provider>)
}

export default App;
