import useInput from '../utils/formValidator'
import {CurrentUserContext} from '../contexts/CurrentUserContext';
import {useContext, useEffect} from 'react';



function PopupWithForm(props) {
  const currentUser = useContext(CurrentUserContext);
  const isLoading = props.onLoading;
  const isOpen = props.isOpen;

  const name = useInput(currentUser.name, isOpen);
  const description = useInput(currentUser.about, isOpen);
  const showDescriptionError = !description.isCorrect && description.error;
  const showNameError = !name.isCorrect && name.error;
  const showDisabledSubmitButton = name.disabledSubmitButton || description.disabledSubmitButton || isLoading;
  //const showDisabledSubmitButton = name.disabledSubmitButton || description.disabledSubmitButton || isLoading;
  return (
    <div className={`popup popup_type_${props.name} ${props.isOpen ? 'popup_show' : ''}`}>
      <div className="popup__window">
        <button type="button" onClick={props.onClose} className={`popup__close-button popup__close-button_${props.name}`}>
          {}
        </button>
        <form onSubmit={props.onSubmit} className={`popup__form popup__form_${props.name}`} name={props.name} id={props.name} noValidate>
          <h2 className={`popup__title ${props.name === 'avatar'? 'popup__title_avatar' : ''}`}>
            {props.title}
          </h2>
          <fieldset className={'popup__fieldset'}>
            {props.children}
            <button type="submit" className={`popup__save-button ${(showDisabledSubmitButton)&& 'popup__save-button_disabled'}`}>
              {isLoading ? 'Удаление...' : `${props.buttonText}`}
            </button>
          </fieldset>
        </form>
      </div>
    </div>
  )
}

export default PopupWithForm;
